// SPDX-License-Identifier: Apache 2.0
pragma solidity ^0.8.7;

// Importing OpenZeppelin's SafeMath Implementation
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "hardhat/console.sol";

import "./Rosca.sol";

contract Spaces {
    using SafeMath for uint256;
    using SafeMath for uint256;

    struct ActiveSpace {
        address spaceAddress;
        string spaceName;
        string spaceType; // rosca, personal, regular, mchango, challenge,
    }

    //List of all spaces
    Rosca[] public roscas;
    mapping(address => ActiveSpace[]) mySpaces;
    mapping(address => mapping(address => uint256)) mySpaceIdx; //starts from 1, 0 means was removed

    //event for when a new rosca is created
    event CreatedRosca(
        address roscaAddress,
        address roscaCreator,
        RoscaDetails RD
    );

    function createRosca(RoscaDetails memory rD) external {
        Rosca newRosca = new Rosca(payable(msg.sender), rD);

        roscas.push(newRosca);
        ActiveSpace memory AS;
        AS.spaceAddress = address(newRosca);
        AS.spaceName = rD.roscaName;
        AS.spaceType = "rosca";
        mySpaces[msg.sender].push(AS); //update spaces list
        mySpaceIdx[msg.sender][address(newRosca)] = mySpaces[msg.sender].length;

        emit CreatedRosca(address(newRosca), msg.sender, rD);
    }

    //Returns only roscas for now
    function returnSpaces() external view returns (Rosca[] memory) {
        return roscas;
    }

    //get myspaces
    function getMySpaces() external view returns (ActiveSpace[] memory) {
        return mySpaces[msg.sender];
    }
}

src/utils.sol.content:
// SPDX-License-Identifier: Apache 2.0
pragma solidity ^0.8.7;

library utils {
    uint constant SECONDS_PER_DAY = 24 * 60 * 60;
    uint constant SECONDS_PER_HOUR = 60 * 60;
    uint constant SECONDS_PER_MINUTE = 60;
    int constant OFFSET19700101 = 2440588;

    uint constant DOW_MON = 1;
    uint constant DOW_TUE = 2;
    uint constant DOW_WED = 3;
    uint constant DOW_THU = 4;
    uint constant DOW_FRI = 5;
    uint constant DOW_SAT = 6;
    uint constant DOW_SUN = 7;

    function _daysFromDate(
        uint year,
        uint month,
        uint day
    ) internal pure returns (uint _days) {
        require(year >= 1970);
        int _year = int(year);
        int _month = int(month);
        int _day = int(day);

        int __days = _day -
            32075 +
            (1461 * (_year + 4800 + (_month - 14) / 12)) /
            4 +
            (367 * (_month - 2 - ((_month - 14) / 12) * 12)) /
            12 -
            (3 * ((_year + 4900 + (_month - 14) / 12) / 100)) /
            4 -
            OFFSET19700101;

        _days = uint(__days);
    }

    function _daysToDate(uint _days)
        internal
        pure
        returns (
            uint year,
            uint month,
            uint day
        )
    {
        int __days = int(_days);

        int L = __days + 68569 + OFFSET19700101;
        int N = (4 * L) / 146097;
        L = L - (146097 * N + 3) / 4;
        int _year = (4000 * (L + 1)) / 1461001;
        L = L - (1461 * _year) / 4 + 31;
        int _month = (80 * L) / 2447;
        int _day = L - (2447 * _month) / 80;
        L = _month / 11;
        _month = _month + 2 - 12 * L;
        _year = 100 * (N - 49) + _year + L;

        year = uint(_year);
        month = uint(_month);
        day = uint(_day);
    }
}
